import { numbro } from '../i18n/i18n'

export function numberFormatter(humanize: boolean, round: boolean) {
  return (value: number) =>
    numbro(value).format({
      thousandSeparated: true,
      average: humanize,
      trimMantissa: true,
      mantissa: round ? 0 : 2,
    })
}
